import React from 'react';
export interface HeaderProps {
    /**
     * Дополнительное содержимое хедера. JSX разметка.
     */
    children?: React.ReactNode;
    /**
     * Ссылка на главную по клику на логотип.
     */
    mainPageLink?: string;
    /**
     * Опциональный логотип партнёра.
     * Необходимо учитывать размеры для корректного отображения.
     */
    partnerLogo?: React.ReactNode;
}
/**
 * Стандартный header для страницы.
 */
declare const Header: {
    ({ mainPageLink, partnerLogo, children }: HeaderProps): JSX.Element;
    displayName: string;
};
export default Header;
