export interface Images {
    url: string;
    id: string | number;
    imgAlt: string;
}
export interface PhotoGalleryProps {
    /**
     * Массив с данными о изображениях.
     * <a href="/?path=/story/design-system-справочник-сложные-типы-данных-в-компонентах--docs">Сложный тип данных</a>
     */
    images: Images[];
    /**
     *  Определяет видимость модального окна. Стейт должен обрабатываться в родительском компоненте.
     *  Чтобы окно закрылось, ему нужно явно передавать isOpen = false
     */
    isOpen: boolean;
    /**
     * Функция для обработки закрытия окна.
     */
    onClose(): void;
}
/**
 * Компонент для просмотра изображений.
 * Работает как модальное окно.
 */
declare const PhotoGallery: {
    ({ images, isOpen, onClose }: PhotoGalleryProps): JSX.Element;
    displayName: string;
};
export default PhotoGallery;
