import React from 'react';
declare type MenuContentPosition = 'right' | 'top' | 'bottom';
export interface MenuStyle {
    tooltipContentPosition: MenuContentPosition;
    style: {
        top: number;
        left: number;
        width: string | number;
    };
}
interface TableCellMenuProps {
    cellTitle: string;
    cellHeader: string;
    isOpen?: boolean;
    handleCopyClick?: () => void | Promise<void>;
    onSaveButtonClick?: (cellRowIndex: number, cellColumnId: string, cellValue: string) => void;
    cellRowIndex: number;
    cellColumnId: string;
}
/**
 * Субкомпонент для отображения в ячейке контекстного меню (появится по клику на иконку).
 * Отдельно от ячейки таблицы не используется.
 */
declare const TableCellMenu: React.FC<TableCellMenuProps>;
export default TableCellMenu;
